        /// <summary> 0: Disable | 1: Enable | 2: Restored | 3: Custom 
        /// </summary>
        /// <param name="PatchType"></param>
        public void ApplyDebugPatches(int PatchType) {
            if(Game == 0) {
                if(!FlashThreadHasStarted) {
                    FlashThread.Start("GameInfoLabel");
                    FlashThreadHasStarted = true;
                }
                LabelShouldFlash = true;
                SetInfoLabelText("Please Select A Game's Executable First");
                InfoHasImportantStr = true;
                return;
            }


            // Enable Debug Mode / Menus  (Also Checks Whether The Game's Tlou R 1.00 Because That's The Only One Without a JNZ)
            WriteByte(DebugAddressForSelectedGame, (byte)(PatchType == 0 ? (DebugAddressForSelectedGame == 0x5C5A ? 0x74 : 0x75) : 0xEB));

            if(PatchType < 2) { // Return If Patch Is Basic Debug Toggle
                SetInfoLabelText($"{ActiveGameID} {ResultStrings[PatchType]}");
                return;
            }

            switch(Game) {
                default:
                    MessageBox.Show("Couldn't Determine The Game This Executable Belongs To, Send It To Blob To Have It's Title ID Supported\n" + Game);
                    break;
                case GameIDs.T1R100:
                case GameIDs.T1R109:
                case GameIDs.T1R110:
                case GameIDs.T1R111:
                    T1R11X_RestoredMenu();
                    break;
                case GameIDs.T2100:
                case GameIDs.T2101:
                case GameIDs.T2102:
                case GameIDs.T2105:
                case GameIDs.T2107:
                    T2107_CustomMenu();
                    break;
                case GameIDs.T2108:
                case GameIDs.T2109:
                    T2109_CustomMenu();
                    break;
                case GameIDs.UC1100: // Uncharted 1 1.00 Restored Debug Ver. 2.6.1
                    UC1100_RestoredMenu();
                    break;
                case GameIDs.UC1102: // Uncharted 1 1.02 Restored Debug Ver. 2.7
                    UC1102_RestoredMenu();
                    break;
                case GameIDs.UC2100: // Uncharted 2 1.00 Restored Debug Ver. 1.0
                    UC2100_RestoredMenu();
                    break;
                case GameIDs.UC2102: // Uncharted 2 1.02 Restored Debug Ver. 1.0
                  //UC2102_RestoredMenu();
                    break;
                case GameIDs.UC3100:
                    UC3100_RestoredMenu();
                    break;
                case GameIDs.UC3102:
                  //UC3100_RestoredMenu();
                    break;
                case GameIDs.UC4100:
                  //UC4SP100_CustomMenu();
                    break;
                case GameIDs.UC4127_133:
                  //UC4SP127_CustomMenu();
                    break;
                case GameIDs.UC4133MP:
